import * as d3 from "d3";
import styles from "../components/ForceGraph.css";
import "@fortawesome/fontawesome-free/css/all.min.css";
import { useEffect } from "react";

export const RunForceGraph = ( container, linksData, nodesData, graph ) => { 
  useEffect(() => { 
    const links = linksData.map((d) => Object.assign({}, d));
    const nodes = nodesData.map((d) => Object.assign({}, d));

    const containerRect = container.getBoundingClientRect();
    // var parentDiv = document.getElementById("parentDiv");
    const height = containerRect.height;
    const width = containerRect.width;

    const color = () => { return "#9D79A0"; };

    const drag = (simulation) => {
      const dragstarted = (d) => {
        if (!d3.event.active) simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
      };

      const dragged = (d) => {
        d.fx = d3.event.x;
        d.fy = d3.event.y;
      };

      const dragended = (d) => {
        if (!d3.event.active) simulation.alphaTarget(0);
        d.fx = null;
        d.fy = null;
      };

      return d3
        .drag()
        .on("start", dragstarted)
        .on("drag", dragged)
        .on("end", dragended);
    };

    const simulation = d3
      .forceSimulation(nodes)
      .force("link", d3.forceLink(links).id(d => d.id).distance(100))
      .force("charge", d3.forceManyBody().strength(-600))
      .force("collide", d3.forceCollide().strength(1).radius(30).iterations(1))
      .force("x", d3.forceX())
      .force("y", d3.forceY());
    const svg = d3
      .select(container)
      .append("svg")
      .attr("viewBox", [-width / 2, -height / 2, width, height])
      .call(d3.zoom().on("zoom", function () {
        svg.attr("transform", d3.event.transform);
    }));


    const link = svg
      .append("g")
      .attr("stroke", "#999")
      .attr("stroke-opacity", 0.6)
      .selectAll("line")
      .data(links)
      .join("line")
      .attr("stroke-width", d => Math.sqrt(d.value));

    const node = svg
      .append("g")
      .attr("stroke", "#fff")
      .attr("stroke-width", 2)
      .selectAll("circle")
      .data(nodes)
      .join("circle")
      .attr("r", 24)
      .attr("fill", color)
      .call(drag(simulation));

    const label = svg.append("g")
      .attr("class", "labels")
      .selectAll("text")
      .data(nodes)
      .enter()
      .append("text")
      .attr('text-anchor', 'middle')
      .attr('dominant-baseline', 'central')
      .text(d => {return d.id;})
      .call(drag(simulation));

    // label.on("mouseover", (d) => {
    //   addTooltip(nodeHoverTooltip, d, d3.event.pageX, d3.event.pageY);
    // })
    //   .on("mouseout", () => {
    //     removeTooltip();
    //   });

    simulation.on("tick", () => {

      // update link positions
      link
        .attr("x1", d => d.source.x)
        .attr("y1", d => d.source.y)
        .attr("x2", d => d.target.x)
        .attr("y2", d => d.target.y);

      // update node positions
      node
        .attr("cx", d => d.x)
        .attr("cy", d => d.y);

      // update label positions
      label
        .attr("x", d => { return d.x; })
        .attr("y", d => { return d.y; })
    });
  }, [])
  return (
    // destroy: () => {
    //   simulation.stop();
    // },
    // nodes: () => {
    //   return svg.node();
    // }
    <svg className="d3-component" ref={d3Container}/>

  );
}
