import * as d3 from "d3";
import styles from "../components/ForceGraph.css";
import "@fortawesome/fontawesome-free/css/all.min.css";
import { useEffect, useRef } from "react";

export const RunForceGraph = ( graph ) => { 
  const d3Container = useRef(null);

  const height = 1200;
  const width = 1200; 
  useEffect(() => { 

    if (graph.links && graph.nodes) {
      const links = graph.graph.links.map((d) => Object.assign({}, d));
      const nodes = graph.graph.nodes.map((d) => Object.assign({}, d));


      // var parentDiv = document.getElementById("parentDiv");


      const color = () => { return "#9D79A0"; };

      const drag = (simulation) => {
        const dragstarted = (d) => {
          if (!d3.event.active) simulation.alphaTarget(0.3).restart();
          d.fx = d.x;
          d.fy = d.y;
        };

        const dragged = (d) => {
          d.fx = d3.event.x;
          d.fy = d3.event.y;
        };

        const dragended = (d) => {
          if (!d3.event.active) simulation.alphaTarget(0);
          d.fx = null;
          d.fy = null;
        };

        return d3
          .drag()
          .on("start", dragstarted)
          .on("drag", dragged)
          .on("end", dragended);
      };

      const simulation = d3
        .forceSimulation(nodes)
        .force("link", d3.forceLink(links).id(d => d.id).distance(100))
        .force("charge", d3.forceManyBody().strength(-600))
        .force("collide", d3.forceCollide().strength(1).radius(30).iterations(1))
        .force("x", d3.forceX())
        .force("y", d3.forceY());

      const svg = d3
        .select(d3Container.current)
        .attr("viewBox", [-width / 2, -height / 2, width, height])
        .call(d3.zoom().on("zoom", function () {
          svg.attr("transform", d3.event.transform);
      }));

      const updateLink = svg
        .append("g")
        .selectAll("line")
        .data(links);

      updateLink.enter()
        .append("line")
        .attr("stroke", "#999")
        .attr("stroke-opacity", 0.6)
        .attr("stroke-width", d => Math.sqrt(d.value));

      const updateNode = svg
        .append("g")
        .selectAll("circle")
        .data(nodes);

      updateNode.enter()
        .append("circle")
        .attr("stroke", "#fff")
        .attr("stroke-width", 2)
        .attr("r", 24)
        .attr("fill", color)
        .call(drag(simulation));

      const updateLabel = svg
        .append("g")
        .selectAll("text")
        .data(nodes);

      updateLabel
        .enter()
        .append("text")
        .attr("class", "labels")
        .attr('text-anchor', 'middle')
        .attr('dominant-baseline', 'central')
        .text(d => {return d.id;})
        .call(drag(simulation));

      // label.on("mouseover", (d) => {
      //   addTooltip(nodeHoverTooltip, d, d3.event.pageX, d3.event.pageY);
      // })
      //   .on("mouseout", () => {
      //     removeTooltip();
      //   });

      simulation.on("tick", () => {

        // update link positions
        updateLink
          .attr("x1", d => d.source.x)
          .attr("y1", d => d.source.y)
          .attr("x2", d => d.target.x)
          .attr("y2", d => d.target.y);

        // update node positions
        updateNode
          .attr("cx", d => d.x)
          .attr("cy", d => d.y);

        // update label positions
        updateLabel
          .attr("x", d => { return d.x; })
          .attr("y", d => { return d.y; })
      });

      updateLink.exit().remove();
      updateNode.exit().remove();
      updateLabel.exit().remove();
    }
  }, [graph, d3Container.current])

  return (
    // destroy: () => {
    //   simulation.stop();
    // },
    // nodes: () => {
    //   return svg.node();
    // }
    <svg width={width} height={height} ref={d3Container}/>

  );
}

export default RunForceGraph;
