import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/system/Stack';
import Button from '@mui/material/Button';
import { useState} from 'react';

const Form = ({graph, setGraph}) => {
    const [importId, setImportId] = useState('');
    const [error, setError] = useState('');

    const handleChange = (event) => {
        setImportId(event.target.value)
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        await fetch(`http://localhost:9092/get-node-by-importId?importId=${importId}`)
        .then((res) => res.json())
        .then((data) => {
            setGraph({
                nodes: data.nodes,
                links: data.links
            });
            setImportId(''); 
            console.log(graph);                                
        })
        .catch((err) => {
            setError('Invalid input!');
            console.log(err.message);
        })
    }

  return (
    // <form onSubmit={handleSubmit}>
    //     <span>
    //         <input type="text" value={importId} onChange={handleChange}></input>
    //         <button type="submit">Submit</button>
    //     </span>
    // </form>
    <div className="form">
        <Box
        // component="form"
        sx={{
            '& > :not(style)': { m: 1, width: '25ch' },
        }}
        noValidate
        autoComplete="off"
        >


            <form onSubmit={handleSubmit}>
                <Stack spacing={4}>
                <TextField id="filled-basic" label="Import Id" variant="filled" type="text" value={importId} onChange={handleChange}/>
                <Button variant="contained" type="submit" >Submit</Button>
                </Stack>
                {error ? <div style={{color: "#cc0000"}}>{error}</div> : ''}
            </form>

            {/* <div className="nodes">
            {nodes.map((node) => (
                <Graph key={node.identity} node={node}/>
            ))}
            </div> */}

        </Box>


    </div>
  )
}

export default Form;
