import InputWindow from "../components/InputWindow";
import OutputWindow from "../components/OutputWindow";
import {useState} from 'react';

import { useEffect } from 'react';
import Box from '@mui/material/Box';
import NetworkDiagramGenerator from './NetworkDiagramGenerator';
import React, { useRef } from "react";
import { ForceGraph } from '../components/ForceGraph';
import data from '../components/Data.json';

const Home = () => {
    const [graph, setGraph] = useState({})

    return (
        <div className="Home">
            <InputWindow graph={graph} setGraph={setGraph}/>
            {/* <OutputWindow graph={graph}/> */}
        </div>
    )

    const nodeHoverTooltip = React.useCallback((node) => {
        return `<div>${node.name}</div>`;
      }, []);

    if (graph.links && graph.nodes){
        return (
            <Box sx={{p: 8}}>
                <Box style={{backgroundColor: "#F2F1F1"}}>
                        {/* <NetworkDiagramGenerator graph={graph} width={1400} height={1500}/> */}
                        <ForceGraph linksData={graph.links} nodesData={graph.nodes} nodeHoverTooltip={nodeHoverTooltip}/>
                </Box>
            </Box>

        )
    }
}

export default Home
